$hex = ""
$bytes = [byte[]]::new($hex.Length / 2)
for ($i = 0; $i -lt $hex.Length; $i += 2) {
    $bytes[$i/2] = [Convert]::ToByte($hex.Substring($i, 2), 16)
}
$decoded = [System.Text.Encoding]::UTF8.GetString($bytes).TrimEnd([char]0)
$decoded | Set-Content -Path "D:\Workstation\Comenon\workstation\coinPayloads\compileengine\loader_decrypted.txt" -Encoding UTF8
Write-Output "Decoded content written to loader_decrypted.txt"
